mapper.select:


     and item_name like concat('%', #{itemName}, '%')
     and item_type  = #{itemType}
     and batch_num like concat('%', #{batchNum}, '%')
     and warranty_time = #{warrantyTime}
     and item_location = #{itemLocation}
     and item_state = #{itemState}
     and item_belonger = #{itemBelonger}
     and is_write_off = #{isWriteOff}
     and is_modify = #{isModify}

order by item_belonger,is_write_off

select sys_dept.dept_name as item_belonger_name,assets_items.item_type,assets_items.batch_num,
count(assets_items.item_type) as item_num,
sum(case when assets_items.is_write_off = 1 then 1 else 0 end) as write_off_num,
sum(case when assets_items.is_write_off = 1 then 0 else 1 end) as residue_num
from assets_items,sys_dept
where assets_items.item_belonger=sys_dept.dept_id
and sys_dept.dept_name=#{itemBelongerName}
and item_type=#{itemType}
and batch_num=#{batchNum}
group by assets_items.item_belonger,assets_items.item_type,assets_items.batch_num

select sys_dept.dept_name from sys_dept,assets_items
where sys_dept.dept_id = assets_items.item_belonger
 and sys_dept.dept_name = #{itemBelongerName}
 and batch_num = #{batchNum}
 and item_type= #{itemType}
group by dept_name

 select batch_num from sys_dept,assets_items
where sys_dept.dept_id = assets_items.item_belonger
and sys_dept.dept_name = #{itemBelongerName}
and batch_num = #{batchNum}
and item_type= #{itemType}
 group by batch_num

select item_type from sys_dept,assets_items
  where sys_dept.dept_id = assets_items.item_belonger
  and sys_dept.dept_name = #{itemBelongerName}
  and batch_num = #{batchNum}
  and item_type= #{itemType}
  group by
  item_type

mapper.update:
update assets_items
item_name = #{itemName}, item_type = #{itemType}, item_format = #{itemFormat}, batch_num = #{batchNum}, supplier_name = #{supplierName}, post_sale_phone = #{postSalePhone}, installed_time = #{installedTime}, warranty_time = #{warrantyTime}, item_location = #{itemLocation}, item_ip = #{itemIp}, item_admin = #{itemAdmin}, is_write_off = #{isWriteOff}, item_state = #{itemState}, remarks = #{remarks}, item_belonger = #{itemBelonger}, create_time = #{createTime}, is_modify = #{isModify},
where id = #{id}
